mapper.select:
SELECT *, (select name from user where userno=comment.userno) name FROM comment
WHERE board_no=#{board_no} AND tablename=#{tablename}
ORDER BY regdate DESC

mapper.insert:
INSERT INTO comment (
	board_no, content, like, regdate, gno, ono, nested, tablename, userno
) values (
	#{board_no}, #{content}, #{like}, NOW(), #{gno}, #{ono}, #{nested}, #{tablename}, #{userno}
)

mapper.update:
UPDATE comment SET
	content=#{content}
WHERE comment_no = #{comment_no}
