mapper.select:
SELECT *, (select name from user where userno=comment.userno) name FROM comment
WHERE board_no=#{board_no} AND tablename=#{tablename}
ORDER BY regdate DESC

mapper.insert:
INSERT INTO comment (
	board_no, userno, content, like, regdate, gno, ono, nested, tablename, concern_board_no, image_board_no, video_board_no
) values (
	#{board_no}, #{userno}, #{content}, #{like}, NOW(), #{gno}, #{ono}, #{nested}, #{tablename}, #{concern_board_no}, #{image_board_no}, #{video_board_no}
)
