mapper.insert:
INSERT INTO friends (
	my_userno, friends_userno
) VALUES (
	#{from_userno}, #{to_userno}
)

	SELECT last_insert_id()

mapper.select:
SELECT user.userno, user.id, user.name, user.gender, user.email, user.profile
FROM friendsadd AS fa JOIN user
ON fa.to_userno = #{to_userno} AND fa.from_userno = user.userno;

SELECT userno, id, name, gender, email, profile FROM user
WHERE userno in (
SELECT friends_userno from friends where my_userno = #{x}
UNION
SELECT my_userno FROM friends WHERE friends_userno = #{x}
)
