mapper.insert:
INSERT INTO image_board (
	userno, title, content, regdate, readcount, category
) values (
	#{userno}, #{title}, #{content}, NOW(), #{readcount}, #{category}
)

	SELECT last_insert_id()

mapper.update:
UPDATE image_board SET
	title=#{title}
	, content=#{content}
WHERE image_board_no = #{image_board_no}
