mapper.insert:
INSERT INTO notice (
	admin_no, title, contents, regdate, readcount, filename_org, filename_real, newnotice
) values (
	#{admin_no}, #{title}, #{contents}, NOW(), #{readcount}, #{filename_org}, #{filename_real}, #{newnotice}
)

mapper.select:
SELECT *,
	(SELECT name FROM admin WHERE admin_no=notice.admin_no) as name
FROM notice WHERE notice_no = #{xxx}

mapper.update:
UPDATE notice SET
	title=#{title}
	, contents=#{contents}
	
	, filename_org=#{filename_org}
	, filename_real=#{filename_real}
	
WHERE notice_no = #{notice_no}
