mapper.insert:
INSERT INTO qna (
	userno, title, content, regdate, filename_org, filename_real, secret
) values (
	#{userno}, #{title}, #{content}, NOW(), #{filename_org}, #{filename_real}, #{secret}
)

mapper.update:
UPDATE qna SET
	title=#{title}
	, content=#{content}
	
	, filename_org=#{filename_org}
	, filename_real=#{filename_real}
	
WHERE qna_no = #{qna_no}
